				if (vol >= 1000)
				{
					vol %= 1000;

					if (vol / 1000 != HUMAN_TypeOfHuman(instance))
					{
						return 0;
					}
				}
			
				SOUND_Play3dSound(&instance->position, soundData->sfxToneID, soundData->pitch, vol, soundData->minVolDistance);

