{
	struct _MonsterVars* mv;

	mv = (struct _MonsterVars*)instance->extraData;

	PhysicsSetVelFromRot(instance, &instance->rotation, mv->speed);

	instance->xAccl = 0;
	instance->yAccl = 0;
	instance->zAccl = 0;

	PhysicsMove(instance, &instance->position, gameTrackerX.timeMult);
}


