void SCRIPT_FadeOutProcess(struct _Instance* instance)
{
	instance->fadeValue += gameTrackerX.timeMult * 32;

	if (instance->fadeValue >= 4096)
	{
		INSTANCE_PlainDeath(instance);
	}
}


